//Bu Kodlar wio.db'ye Aittir ~Beş
const green = (message) => `\x1b[32m${message}\x1b[0m`;
const red = (message) => `\x1b[31m${message}\x1b[0m`;
const yellow = (message) => `\x1b[33m${message}\x1b[0m`;
const advertisement = `${yellow("( five.db ) => Bilgilendirme:")} ${green("Destek İçin; https://discord.gg/luppux")}`;

class DatabaseError extends Error {
    constructor(message) {
        super(`${red(message)}\n${advertisement}`);
    }
    get name() {
        return yellow(`( five.db ) ${this.constructor.name}`);
    }
}

module.exports = DatabaseError;